import styled, {keyframes} from 'styled-components'

export const PaymentContainer = styled.div`
        width: 980px;
        height: 100%;
        display: flex;
        flex-direction: column;
`;

export const InputBlock = styled.div`
        display: flex;
        flex-direction: column;
`;

export const Input = styled.input`
    width: 200px;
    padding: 10px 25px;
    font-size: 18px;
    border: none;
    border-radius: 5px;
`;
