import styled, {keyframes} from 'styled-components'
import InputMask from "react-input-mask";

export const PaymentContainer = styled.div`
        width: 980px;
        height: 100%;
        display: flex;
`;

export const Input = styled(InputMask)`
    width: 250px;
`;
