import {useState, useEffect} from 'react'; 
import {PaymentContainer, Input, InputBlock} from '../../components/PaymentPageStyled';

const PaymentPage = ({data}) => {
    //для избежания предупреждения useLayoutEffect на стороне сервера
    const [showChild, setShowChild] = useState(false);

    const [phone, setPhone] = useState('');
    const [payment, setPayment] = useState(0);

    useEffect(() => { setShowChild(true) }, []);

    function sendData(e) {
        //проверяет наличие телефона и суммы оплаты, если ок, то отправляет запрос на фейк апи
        e.preventDefault();
        if(phone && payment) {
            validatePhone(phone);
            console.log(phone, payment);
        } alert('Заполните все поля!')
    }

    function handlePhoneChange(e) {
        setPhone(e.target.value)
    }

    function handlePaymentChange(e) {
        validatePayment(e.target.value);
    }

    //убирает из строки телефона все лишние символы
    function validatePhone(str) {
        const regExp = /\d/g;
        const validatedArr =  str.match(regExp);
        const validated = validatedArr.join('');
        return validated;
    }

    //проверяет введенную сумму на заданные параметры range = [1, 1000] если ок, то обновляет состояние
    function validatePayment(str, range) {
        const payment = str;
        console.log(typeof payment)
    }

    
    if (!showChild) {
        return null;
    }else if (data && data.id) {
        return (
            <PaymentContainer>  
                <h1>Оплата {data.operatorName}</h1>    
                <form>
                    <InputBlock>
                        <label htmlFor="phone">Номер телефона</label>
                        <Input 
                            mask="+7(999)999-99-99" 
                            id="phone" 
                            type="text" 
                            onChange={(e)=>{handlePhoneChange(e)}}>
                        </Input>
                        <span></span>
                    </InputBlock>
                    <InputBlock>
                        <label htmlFor="payment">Сумма платежа</label>
                        <Input
                            maskPlaceholder=""
                            mask="9999" 
                            id="payment" 
                            type="text"
                            onChange={(e)=>{handlePaymentChange(e)}}>      
                        </Input>
                        <span></span>    
                    </InputBlock>
                    in
                    <button onClick={(e)=> {sendData(e)}}>go</button>
                </form>
            </PaymentContainer>
        )
    } else {
    return <h1>{data.errMessage}</h1>
    }
}

PaymentPage.getInitialProps = async (ctx) => {
    const res = await fetch(`http://localhost:3000/api/operators/${ctx.query.payOperatorID}`);
    const data = await res.json();
    return { data }
}

export default PaymentPage
