import {useState} from 'react'; 
import {PaymentContainer, InputBlock,Input} from '../../components/PaymentPageStyled';
import FormInput from '../../components/inputComponent/FormInput'

const PaymentPage = ({data}) => {

    //добавить логические состояния validphone validpayment чтобы включать выключать кнопку
    const [phone, setPhone] = useState('');
    const [validPhone, setValidPhone] = useState(false);
    const [payment, setPayment] = useState('');
    const [validPayment, setValidPayment] = useState(false);
    const [validForm, setValidForm] = useState(false)
    //сделать логику для отключения кнопки


    function sendData(e) {
        console.log('work');
        e.preventDefault();
    }

    if (data && data.id) {
        return (
            <PaymentContainer>  
                <h1>Оплата {data.operatorName}</h1>    
                <form>
                    <InputBlock>
                        <label htmlFor="phone">Номер телефона</label>
                        <Input 
                            id="phone" 
                            value="phone" 
                            type="tel"
                            mask="+7(999)-999-99-99"
                        />
                        <div>От</div>
                    </InputBlock>
                    
                    <input type="submit" value="Отправить" onClick={(e)=> {sendData(e)}}></input>
                </form>
            </PaymentContainer>
        )
    } else {
    return <h1>{data.errMessage}</h1>
    }
}

PaymentPage.getInitialProps = async (ctx) => {
    const res = await fetch(`http://localhost:3000/api/operators/${ctx.query.payOperatorID}`);
    const data = await res.json();
    return { data }
}

export default PaymentPage

const validationPhoneEvents = {
    valid: {
        value: 'Верно!!!', 
        bool: true
    },
    invalid: {
        value: 'Заполните поле до конца',
        bool: false
    }
}

const validationPaymentEvents = {
    valid: {
        value: 'Верно!!!', 
        bool: true
    },
    invalid: {
        value: 'Введите сумму от 1 до 1000 рублей',
        bool: false
    }
}
