import {Input, InputBlock} from './FormInputStyled';
import {useState, useEffect} from 'react';


const FormInput = (props) => { 
    //для избежания предупреждения useLayoutEffect на стороне сервера
    const [showChild, setShowChild] = useState(false);
    useEffect(() => { setShowChild(true) }, []);

    const [valid, setValid] = useState(false);
    const [value, setValue] = useState('');


    function handleInputValue(e) {
        if(value === e.target.value) {
            return true
        }else if(props.regExp.test(e.target.value)) {
            const validVal = props.validateMethod(e.target.value);
            props.stateCallback(validVal);
            setValue(e.target.value);
            setValid(true);  
        } else {
            const invalidVal = props.validateMethod(e.target.value);
            props.stateCallback(invalidVal);
            setValue(e.target.value);
            setValid(false);
        }
    }

    if (!showChild) {
        return null
    }else return(
        <InputBlock>
            <label htmlFor={props.id}>{props.labelName}</label>
            <Input
                id={props.id}
                type={props.type}
                placeholder={props.placeholder}
                mask={props.mask || ''}
                onChange={(e)=> handleInputValue(e)}>
            </Input>
            <div>{valid ? props.validationEvents.valid.value : props.validationEvents.invalid.value}</div>
        </InputBlock>
    )
}

export default FormInput
