import styled from 'styled-components'

   export const AppContainer = styled.div`
        min-height: 100vh;
        width: 100%;
        display: flex;
        flex-direction: column;
    `;

    export const MainContainer = styled.main`
        flex: 1 0 auto;
        align-self: center;
        min-height: 100%
    `;
