import db from '../public/db.json'
import { useState } from 'react'



const MainPage = ( { data } ) => {
    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }

    const [operatorsList, setOperatorsList] = useState(data)
    console.log(operatorsList);

    function handleListChange() {
        const updatedList = operatorsList.push(newOperator);
        setOperatorsList(updatedList);
        console.log(operatorsList);
    }

    const listItems = operatorsList.map((operator) =>
        <div key={operator.id}>{operator.id}</div>
    );

    return (
        <>
        <div>
            {listItems}
        </div>
            <button onClick={(e) => handleListChange()}>ooooo</button>
        </>
    )

}

MainPage.getInitialProps = async () => {
    const res = db;
    return {data: res}
}

export default MainPage
