import db from '../public/db.json'
import { useState } from 'react'



const MainPage = ( { data } ) => {
    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }
    const [operatorsList, setOperatorsList] = useState(data)
    const [counter, increment] = useState(0)


    const listItems = operatorsList.map((operator) =>
        <div key={operator.id}>{operator.id}</div>
    );

    console.log(listItems)

    return (
        <>
        <div>
            {listItems}
            {counter}
        </div>
            <div onClick={() => setOperatorsList(operatorsList.push(newOperator))}>ooooo</div>
            <div onClick={() => increment(counter+1)}>++++++</div>
        </>
    )

}

MainPage.getInitialProps = async () => {
    const res = db;
    return {data: res}
}

export default MainPage
