import OperatorTile from "../components/operatorTileComponent/OperatorTile";
import db from '../public/db.json'
import { useState } from 'react'



const MainPage = ( { data } ) => {
    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }

    const [operatorsList, setOperatorsList] = useState(data)
    console.log(operatorsList);

    function handleListChange() {
        const updatedList = operatorsList.push(newOperator);
        setOperatorsList(updatedList);
        console.log(operatorsList)
    }

    

    return (
        <>
        <div>
            {operatorsList.map((user) => {
                return (
                    <div key={user.id}>
                        <OperatorTile props={user}/>
                    </div>
                )
            })}
        </div>
            <button onClick={() => handleListChange()}>ooooo</button>
        </>
    )

}

MainPage.getInitialProps = async () => {
    const res = db;
    return {data: res}
}

export default MainPage
