import OperatorsList from "../components/operatorsListComponent/OperatorsList"
import React, { useState } from 'react'
import db from "../public/db.json";

const MainPage = ({data}) => {
    const [counter, increment] = useState(0)

    const list = data;

    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }
    const [operatorsList, setOperatorsList] = useState(list);
    console.log(list)

    const handleChange = ()=> {
        const list = operatorsList;
        list.push(newOperator);
        console.log(list)
        setOperatorsList(list);

    }

    console.log(operatorsList)
    return (
        <>
        <div>
            {/*<OperatorsList dataList={data}/>*/}
            {operatorsList.map((operator)=> {
                return (
                    <div key={operator.id}>
                        {operator.operatorName}
                    </div>
                )
            })}
        </div>
            <button onClick={()=>handleChange()}>++++++</button>
            <pre>
                {JSON.stringify(operatorsList, null, 4)}
            </pre>
        </>
    )
}

MainPage.getInitialProps = async () => {
    const res = db;
    return {data: res}
}


export default MainPage
