import OperatorsList from "../components/operatorsListComponent/OperatorsList"
import React, { useState } from 'react'
import db from "../public/db.json";

const MainPage = ({data}) => {
    const [counter, increment] = useState(0)

    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }
    const [operatorsList, setOperatorsList] = useState(data);
    let count = 0;
    function handleChange() {
        const list = operatorsList;
        operatorsList.push(newOperator)
        setOperatorsList(list);
        count = 1;
    }

    console.log(operatorsList)
    return (
        <>
        <div>
            {/*<OperatorsList dataList={data}/>*/}
            {operatorsList.map((operator)=> {
                return (
                    <div key={operator.id}>
                        {operator.operatorName}
                    </div>
                )
            })}
        </div>
            <div onClick={() => handleChange()}>++++++</div>
            {count}
        </>
    )
}

MainPage.getInitialProps = async () => {
    const res = db;
    return {data: res}
}


export default MainPage
