import {useState} from 'react'; 
import {PaymentContainer} from '../../components/PaymentPageStyled';
import FormInput from '../../components/inputComponent/FormInput'

const PaymentPage = ({data}) => {

    //добавить логические состояния validphone validpayment чтобы включать выключать кнопку
    const [phone, setPhone] = useState('');
    const [payment, setPayment] = useState('');
    //сделать логику для отключения кнопки


    if (data && data.id) {
        return (
            <PaymentContainer>  
                <h1>Оплата {data.operatorName}</h1>    
                <form>
                    <FormInput
                        labelName={'Номер телефона'}
                        id={"phone"}
                        type={"tel"}
                        placeholder={"+7(___)-___-__-__"}
                        mask={"+7(999)-999-99-99"}
                        regExp={/\+7\s?[\(]{0,1}9[0-9]{2}[\)][-]{0,1}\s?\d{3}[-]{0,1}\d{2}[-]{0,1}\d{2}/g}
                        validationEvents = {validationPhoneEvents}
                        validateMethod = {validatePhone}
                        stateCallback = {updatePhoneValue}
                    />
                    <FormInput
                        labelName={"Сумма оплаты"}
                        id={"payment"}
                        type="text"
                        placeholder={"Введите сумму платежа"}
                        mask={"9999"}
                        regExp={/\d{4}/}
                        validationEvents = {validationPaymentEvents}
                        validationMethod = {validatePayment}
                        setStateCallbacl = {updatePaymentValue}
                    />
                    <input type="submit" value="Отправить" onClick={(e)=> {sendData(e)}}></input>
                </form>
            </PaymentContainer>
        )
    } else {
    return <h1>{data.errMessage}</h1>
    }
}

PaymentPage.getInitialProps = async (ctx) => {
    const res = await fetch(`http://localhost:3000/api/operators/${ctx.query.payOperatorID}`);
    const data = await res.json();
    return { data }
}

export default PaymentPage

const validationPhoneEvents = {
    valid: {
        value: 'Верно!!!', 
        bool: true
    },
    invalid: {
        value: 'Заполните поле до конца',
        bool: false
    }
}

const validationPaymentEvents = {
    valid: {
        value: 'Верно!!!', 
        bool: true
    },
    invalid: {
        value: 'Введите сумму от 1 до 1000 рублей',
        bool: false
    }
}
