import {Input, InputBlock} from './FormInputStyled';
import {useState, useEffect} from 'react';

// id mask pattern placeholder type callback

const FormInput = (props) => { 
    //для избежания предупреждения useLayoutEffect на стороне сервера
    const [showChild, setShowChild] = useState(false);
    useEffect(() => { setShowChild(true) }, []);

    if (!showChild) {
        return null;
    }else return(
        <InputBlock>
            <label htmlFor={props.id}>Номер телефона</label>
            <Input
                labelContent={pr}
                id={props.id}
                type={props.type}
                placeholder={props.placeholder}
                mask={props.mask}
                onChange={props.onChange}>
            </Input>
            <div>отработка ошибок</div>
        </InputBlock>
    )
}

export default FormInput
