import db from "../../public/db.json";
import { useState } from 'react'

const OperatorsList = ({...props}) => {
    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }
    const [operatorsList, setOperatorsList] = useState(props.dataList)

    function handleChange() {
        const dataList = operatorsList;
        const [ newList ] = dataList.push(newOperator);
        console.log(newList)
        setOperatorsList(newList)
    }

    console.log(operatorsList)
       return(
        <div>
            {
                operatorsList.map((operator)=> {
                    return (
                        <div key={operator.id}>
                            {operator.operatorName}
                        </div>
                    )
                })
            }
            <button onClick={()=> handleChange()}>+++</button>
        </div>
    )
}


export default OperatorsList
