import {useState} from 'react'; 
import {PaymentContainer} from '../../components/PaymentPageStyled';
import FormInput from '../../components/inputComponent/FormInput'

const validationPhoneEvents = {
    valid: {
        value: 'Верно!!!', 
        valid: true
    },
    invalid: {
        value: 'Заполните пое до конца',
        valid: false
    }
}

const PaymentPage = ({data}) => {

    const [phone, setPhone] = useState('');
    const [payment, setPayment] = useState('');

    function sendData(e) {
        //проверяет наличие телефона и суммы оплаты, если ок, то отправляет запрос на фейк апи
        e.preventDefault();
        if(phone && payment) {
            console.log(phone, payment);
        } else {alert('Заполните все поля!')}
    }

    //убирает из строки телефона все лишние символы
    function validatePhone(value) {
        const validatedArr =  value.match(/\d/g);
        const validated = validatedArr.join('');
        if(validated.length == 11) {
            console.log(validated);
            return true;
        } else {
            return false;
        };
    }

    function updatePhoneValue(value) {
        setPhone(value)
    }

    //проверяет введенную сумму на заданные параметры range = [min, max] если ок, то обновляет состояние
    function validatePayment(str, range) {
        const num = str*0
        if(num >= 1 || num <= 1000) {
            console.log('valid')
        } else {
            console.log('invalid')
        }
    }

    function updatePaymentValue(value) {
        setPayment(value)
    }

    if (data && data.id) {
        return (
            <PaymentContainer>  
                <h1>Оплата {data.operatorName}</h1>    
                <form>
                    <FormInput
                        labelName={'Номер телефона'}
                        id={"phone"}
                        type={"tel"}
                        placeholder={"+7(___)-___-__-__"}
                        mask={"+7(999)-999-99-99"}
                        regExp={/\+7\s?[\(]{0,1}9[0-9]{2}[\)][-]{0,1}\s?\d{3}[-]{0,1}\d{2}[-]{0,1}\d{2}/g}
                        validEvents = {validationPhoneEvents}
                        validateMethod = {validatePhone}
                        stateCallback = {updatePhoneValue}
                    />
                    <input type="submit" value="Отправить" onClick={(e)=> {sendData(e)}}></input>
                </form>
            </PaymentContainer>
        )
    } else {
    return <h1>{data.errMessage}</h1>
    }
}

PaymentPage.getInitialProps = async (ctx) => {
    const res = await fetch(`http://localhost:3000/api/operators/${ctx.query.payOperatorID}`);
    const data = await res.json();
    return { data }
}

export default PaymentPage
