import React, { useState } from 'react'
import { TileContainer } from "./OperatorsListStyled";
import OperatorTile from "../operatorTileComponent/OperatorTile";

const OperatorsList = ({...props}) => {
    const newOperator = {
        "id": "4",
        "operatorName": "yota",
        "slogan": "ya yo ta"
    }
    const [operatorsList, setOperatorsList] = useState(props.dataList);

    function handleChange() {
        setOperatorsList(
        operatorsList => {
            return [...operatorsList, newOperator]
        });
    }

       return(
        <TileContainer>
            {
            operatorsList.map((operator)=> {
                return (
                    <div key={operator.id}>
                        <OperatorTile
                            operatorName={operator.operatorName}
                            operatorId={operator.id}
                            slogan={operator.slogan}
                        />
                    </div>
                )
            })
            }
            <button onClick={()=> handleChange()}>+++</button>
        </TileContainer>
    )
}


export default OperatorsList
