import React, { useState, useContext} from 'react'
import {Context} from '../Context' 
import { TileContainer, Button, Test } from "../components/OperatorsListStyled";
import OperatorTile from "../components/operatorTileComponent/OperatorTile";


const newOperator = {
    "id": "4",
    "operatorName": "Yota",
    "operatorLink": "yota",
    "slogan": "ya yo ta"
}



const MainPage = ({data}) => {
    const [isVisible, setIsVisible] = useState(false);
    const [operatorsList, setOperatorsList] = useState(data);
    const { hideShowBackground } = useContext(Context)

    async function handleChange() {
        const response = await fetch(
        'http://localhost:3000/api/operators/addOperator', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
              },
              body: JSON.stringify(newOperator)
        })
        const result = await response.json();
        setOperatorsList(
        operatorsList => {
            return [...operatorsList, result]
        });
    }

    function showAddor() {
        console.log(isVisible)
        setIsVisible(!isVisible);
        console.log(isVisible)
        hideShowBackground(isVisible);
    }

    console.log(typeof hi);

    return (
            <TileContainer>
                {
                operatorsList.map((operator)=> {
                    return (
                        <OperatorTile
                            key={operator.id}
                            id={operator.id}
                            name={operator.operatorName}
                            link={operator.operatorLink}
                            imgUrl={operator.imgUrl}
                            slogan={operator.slogan}
                        />
                    )
                })
                }
            <Button onClick={'fff'}>sssss</Button>
            <Test visible={isVisible}>{`${isVisible}`}</Test>
        </TileContainer>
    )
}

MainPage.getInitialProps = async () => {
    const res = await fetch('http://localhost:3000/api/operators');
    const data = await  res.json();
    return {data: data}
}


export default MainPage
