import styled from 'styled-components'

export const AppContainer = styled.div`
    min-height: 100vh;
    width: 100%;
    display: flex;
    flex-direction: column;
`;

export const MainContainer = styled.main`
    width: 980px;
    flex: 1;
    align-self: center;
`;
